import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person'

class App extends Component {

  state = {
    person:[
      {name: 'Steven', age: 23},
      {name: 'Mike', age: 17},
      {name: 'Richard', age: 63}
    ]
  }

  render() {
    return (
      <Person name={this.state.person[1].name} age={this.state.person[1].age}>Nested text in Person</Person>
     );
    }

}

export default App;
